import 'package:flutter/cupertino.dart';
import 'package:flutter/material.dart';
import 'package:flutter_bloc/flutter_bloc.dart';
import 'package:project/application/search/search_bloc.dart';
import 'package:project/core/colors/colors.dart';
import 'package:project/core/constants.dart';
import 'package:project/presentation/widgets/main_title.dart';

const imageurl = [
  'data:image/jpeg;base64,',
  "data:image/jpeg;base64,",
  "data:image/jpeg;base64,",
];

class SearchIdeal extends StatelessWidget {
  const SearchIdeal({super.key});

  @override
  Widget build(BuildContext context) {
    return SafeArea(
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Text(
            'Top Searches',
            style: TextStyle(
              fontSize: 20,
              fontWeight: FontWeight.w900,
            ),
          ),
          kheight,
          Expanded(
            child: BlocBuilder<SearchBloc, SearchState>(
              builder: (context, state) {
                if (state.isloading) {
                  return const Center(
                    child: CircularProgressIndicator(),
                  );
                } else if (state.isError) {
                  return const Center(child: Text('Error'));
                } else if (state.idleList.isEmpty) {
                  return const Center(
                    child: Text('no medias are found'),
                  );
                } else
                  return ListView.separated(
                    shrinkWrap: true,
                    itemBuilder: (ctx, index) {
                      final movie = state.idleList[index];
                      return TopSearchTile(
                          title: movie.title ?? "no title provided",
                          url: imageappendurl + '${movie.posterpath}');
                    },
                    separatorBuilder: (ctx, index) => kheight20,
                    itemCount: state.idleList.length,
                  );
              },
            ),
          )
        ],
      ),
    );
  }
}

class TopSearchTile extends StatelessWidget {
  final String title;
  final String url;
  const TopSearchTile({
    super.key,
    required this.title,
    required this.url,
  });

  @override
  Widget build(BuildContext context) {
    final screenwidth = MediaQuery.of(context).size.width;

    return Row(
      children: [
        Container(
          height: 65,
          width: screenwidth * .3,
          decoration: BoxDecoration(
            image: DecorationImage(
              image: NetworkImage(
                imageurl[0],
              ),
              fit: BoxFit.cover,
            ),
          ),
        ),
        SizedBox(
          width: 10,
        ),
        Expanded(
          child: MainTitle(title: title),
        ),
        CircleAvatar(
          backgroundColor: kwhitecolor,
          radius: 25,
          child: CircleAvatar(
            backgroundColor: Colors.black,
            radius: 23,
            child: Icon(
              CupertinoIcons.play_fill,
              color: kwhitecolor,
            ),
          ),
        )
      ],
    );
  }
}
